CREATE OR REPLACE FUNCTION obter_se_ultrapassou_temp_serv ( nr_sequencia_p bigint, nr_seq_unid_p bigint, dt_param_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_min_previsto_w	bigint;
nr_seq_servico_w	bigint;
dt_inicio_w		timestamp;
dt_total_w		timestamp;
retorno_w		varchar(1) := 'N';

BEGIN

select	coalesce(max(nr_seq_servico),0)
into STRICT	nr_seq_servico_w
from	sl_unid_atend
where	nr_sequencia  = nr_seq_unid_p;

	if (coalesce(nr_sequencia_p,0) = 0) then

		select	coalesce(max(qt_min_previsto),0)
		into STRICT	qt_min_previsto_w
		from  	sl_servico
		where	nr_sequencia = nr_seq_servico_w;

	else
		select	coalesce(max(qt_min_previsto),0)
		into STRICT	qt_min_previsto_w
		from  	sl_servico
		where	nr_sequencia = nr_sequencia_p;

	end if;

	if (qt_min_previsto_w	> 0) then

		select	max(dt_inicio)
		into STRICT	dt_inicio_w
		from	sl_unid_atend
		where	nr_sequencia  = nr_seq_unid_p;

		if (dt_inicio_w IS NOT NULL AND dt_inicio_w::text <> '') then

			select (dt_inicio_w +  qt_min_previsto_w/1440)
			into STRICT	dt_total_w
			;

			if (dt_total_w	< dt_param_p) then
				retorno_w := 'S';
			end if;

		end if;
	end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
