CREATE OR REPLACE FUNCTION obter_se_ult_prescr_rec_adep (nr_atendimento_p bigint, nr_prescricao_p bigint, cd_recomendacao_p bigint, ds_recomendacao_p text, cd_intervalo_p text, qt_recomendacao_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


nr_prescricao_w	bigint;
ie_ultima_w	varchar(1) := 'S';


BEGIN

select	coalesce(max(a.nr_prescricao),0)
into STRICT	nr_prescricao_w
from	prescr_medica b,
	prescr_recomendacao a
where	b.nr_prescricao								= a.nr_prescricao
and	coalesce(a.ds_horarios::text, '') = ''
and	coalesce(to_char(a.cd_recomendacao),a.ds_recomendacao)				= coalesce(to_char(cd_recomendacao_p),ds_recomendacao_p)
and	coalesce(a.cd_intervalo,'XPTO')							= coalesce(cd_intervalo_p,'XPTO')
and	coalesce(a.qt_recomendacao,1)							= coalesce(qt_recomendacao_p,1)
and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicial_p,dt_final_p)	= 'S'
and	b.nr_atendimento										= nr_atendimento_p;

if (nr_prescricao_p = nr_prescricao_w) then
	ie_ultima_w := 'S';
else
	ie_ultima_w := 'N';
end if;

return ie_ultima_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
