CREATE OR REPLACE FUNCTION obter_se_unidade_ativa_data ( dt_parametro_p timestamp, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1);
nr_seq_hist_w	bigint;
qt_reg_w	bigint;

BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_hist_w
from	unidade_atend_hist
where	nr_seq_unidade = nr_seq_interno_p
and	trunc(dt_parametro_p,'dd') >= trunc(dt_historico,'dd');

if (nr_seq_hist_w > 0) then
	select 	max(ie_situacao)
	into STRICT	ie_retorno_w
	from	unidade_atend_hist
	where	nr_sequencia = nr_seq_hist_w;
else
	select	coalesce(count(*),0)
	into STRICT	qt_reg_w
	from	unidade_atend_hist
	where	nr_seq_unidade = nr_seq_interno_p;

	if (qt_reg_w > 0) then
		ie_retorno_w := 'A';
	else
		Select	coalesce(max(ie_situacao),'A')
		into STRICT	ie_retorno_w
		from	unidade_Atendimento
		where	nr_Seq_interno = nr_seq_interno_p;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
