CREATE OR REPLACE FUNCTION obter_se_unidade_bloq (nr_seq_unidade_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';

BEGIN

if (coalesce(nr_seq_unidade_p,0) > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	unidade_atendimento a
	where	a.nr_seq_interno = nr_seq_unidade_p
	and		(a.nr_seq_unid_bloq IS NOT NULL AND a.nr_seq_unid_bloq::text <> '')
	AND		NOT EXISTS (SELECT 1 FROM unidade_atendimento b WHERE b.nr_atendimento = nr_atendimento_p AND a.nr_seq_unid_bloq = b.nr_seq_interno);
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
