CREATE OR REPLACE FUNCTION obter_se_unid_med_compativel (cd_material_p bigint, cd_unid_med_p text) RETURNS varchar AS $body$
DECLARE


ie_compativel_w	varchar(1);


BEGIN
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_unid_med_p IS NOT NULL AND cd_unid_med_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_compativel_w
	from	unidade_medida_dose_v
	where	cd_material		= cd_material_p
	and	cd_unidade_medida	= cd_unid_med_p
	and	ie_prioridade		not in (8,9);

end if;

return ie_compativel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
