CREATE OR REPLACE FUNCTION obter_se_uni_case_planejado ( cd_tipo_acomodacao_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w					varchar(1) := 'S';
ie_case_w						varchar(1) := 'N';
ie_atendimento_planejado_w		varchar(1) := 'N';
ie_sem_acomodacao_w				varchar(1) := 'N';


BEGIN

if (coalesce(cd_tipo_acomodacao_p,0) > 0 and coalesce(nr_atendimento_p,0) > 0) then

	Select  coalesce(max('S'),'N')
	into STRICT	ie_case_w
	from	episodio_paciente a,
			atendimento_paciente b
	where	b.nr_seq_episodio = a.nr_sequencia
	and		b.nr_atendimento = nr_atendimento_p;
	
	if ( ie_case_w = 'S') then	
		
		Select 	obter_se_atendimento_futuro(nr_atendimento_p),
				obter_se_sem_acomodacao(cd_tipo_acomodacao_p)
		into STRICT	ie_atendimento_planejado_w,
				ie_sem_acomodacao_w
		;
		
		if ( ie_atendimento_planejado_w = 'S') then
		
			ie_retorno_w := ie_sem_acomodacao_w;
			
		end if;
	
	
	end if;
	
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
