CREATE OR REPLACE FUNCTION obter_se_usa_grupo ( ie_pasta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN
if (ie_pasta_p = 1) then
	select	CASE WHEN sum(qt_registro)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from (	SELECT 	count(*) qt_registro
				from	procedimento_rotina
				where	(nr_seq_grupo IS NOT NULL AND nr_seq_grupo::text <> '')
				
union

				SELECT	count(*) qt_registro
				from	procedimento_rotina_grupo
				
union

				select	count(*) qt_registro
				from	exame_lab_rotina
				where	(nr_seq_grupo IS NOT NULL AND nr_seq_grupo::text <> '')
				
union

				select	count(*) qt_registro
				from	exame_rotina_grupo) alias8;
elsif (ie_pasta_p = 2) then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	rotina_exame
	where	(nr_seq_grupo IS NOT NULL AND nr_seq_grupo::text <> '');
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
