SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_uso_conv_reg_retorno ( cd_convenio_p bigint, cd_usuario_conv_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ds_retorno_w	varchar(10);
					

BEGIN

begin
	select	nr_sequencia
	into STRICT	qt_existe_w
	from	regra_atend_retorno
	where	cd_convenio = cd_convenio_p
	and	substr(cd_usuario_conv_p, nr_pos_inicial, (nr_pos_final - nr_pos_inicial) + 1) = cd_usuario_padrao
	and clock_timestamp() between coalesce(dt_inicio_vigencia, clock_timestamp()) and coalesce(dt_fim_vigencia, clock_timestamp())
	and coalesce(ie_situacao,'A') = 'A'  LIMIT 1;
exception
	when	others then
		qt_existe_w := 0;
end;

ds_retorno_w := 'N';
if (qt_existe_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
