CREATE OR REPLACE FUNCTION obter_se_usuario_adm (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_existe_w	smallint;


BEGIN

select	coalesce(max(1),0)
into STRICT	qt_existe_w
from	funcao_perfil b,
	usuario_perfil a
where	a.cd_perfil	= b.cd_perfil
and	a.nm_usuario	= nm_usuario_p
and	b.cd_funcao	= 6001;

if (qt_existe_w = 1) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
