CREATE OR REPLACE FUNCTION obter_se_usuario_ausente ( nm_usuario_p text, dt_dia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15) := 'N';


BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	ausencia_tasy
where	nm_usuario_ausente = nm_usuario_p
and	coalesce(substr(obter_se_data_periodo(coalesce(dt_dia_p,clock_timestamp()), dt_inicio, dt_fim),1,1),'N') = 'S';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
