CREATE OR REPLACE FUNCTION obter_se_usuario_aval_perf (nm_usuario_p text, nr_seq_avaliacao_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_perfil_w	varchar(01);
cd_perfil_w	bigint;

c01 CURSOR FOR 
	SELECT	cd_perfil 
	from	usuario_perfil 
	where	nm_usuario	= nm_usuario_p;


BEGIN 
 
ie_perfil_w	:= 'N';
 
open	c01;
loop 
fetch	c01 into cd_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (ie_perfil_w	= 'N') then 
		begin 
 
		select	Obter_Se_Avaliacao_Perfil(cd_perfil_w, nr_seq_avaliacao_p,cd_setor_atendimento_p) 
		into STRICT	ie_perfil_w 
		;
 
		end;
	end if;
 
	end;
end loop;
close c01;
 
return	ie_perfil_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
