CREATE OR REPLACE FUNCTION obter_se_usuario_caixa (nm_usuario_param_p text, nr_seq_caixa_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
0 - Se caixa liberado para usuário
1 - Se usuário caixa pode alterar o caixa
*/
nr_seq_caixa_w		bigint;
ie_atualizar_retorno_w	varchar(1) := 'S';
ie_liberado_w		varchar(1) := 'S';
ie_atualizar_w		varchar(1);
ds_retorno_w		varchar(1);
cont_w			integer;
cd_estabelecimento_w	smallint;
ie_usuario_caixa_w	varchar(1);
qt_caixa_lib_w		bigint;
qt_caixa_reg_w		bigint;
cd_perfil_w		integer;
cd_estab_logado_w	smallint;

c01 CURSOR FOR
SELECT	ie_atualizar
from	usuario_caixa
where	cd_estabelecimento			= cd_estab_logado_w
and	coalesce(cd_perfil,cd_perfil_w)		= cd_perfil_w
and	nm_usuario_param			= nm_usuario_param_p
and	coalesce(nr_seq_caixa,nr_seq_caixa_p)	= nr_seq_caixa_p

union

select	a.ie_atualizar
from	usuario_caixa a
where	a.cd_estabelecimento			= cd_estab_logado_w
and	not exists (select	1
	from	usuario_caixa x
	where	x.cd_estabelecimento			= cd_estab_logado_w
	and	coalesce(x.cd_perfil,cd_perfil_w)		= cd_perfil_w
	and	x.nm_usuario_param			= nm_usuario_param_p
	and	coalesce(x.nr_seq_caixa,nr_seq_caixa_p)	= nr_seq_caixa_p)
and	coalesce(nm_usuario_param::text, '') = ''
and	a.cd_perfil		= cd_perfil_w
and (nr_seq_caixa		= nr_seq_caixa_p or coalesce(nr_seq_caixa::text, '') = '');


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	caixa
where	nr_sequencia	= nr_seq_caixa_p;

cd_perfil_w		:= obter_perfil_ativo;
cd_estab_logado_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select	count(*)
into STRICT	cont_w
from	usuario_caixa
where	cd_estabelecimento		= cd_estab_logado_w
and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
and	nm_usuario_param		= nm_usuario_param_p;

if (cont_w = 0) then

	select	count(*)
	into STRICT	cont_w
	from	usuario_caixa a
	where	a.cd_estabelecimento	= cd_estab_logado_w
	and	coalesce(a.nm_usuario_param::text, '') = ''
	and	a.cd_perfil		= cd_perfil_w;

end if;

select	coalesce(max(ie_usuario_caixa),'L')
into STRICT	ie_usuario_caixa_w
from	parametro_tesouraria
where	cd_estabelecimento	= cd_estabelecimento_w;

select	count(*)
into STRICT	qt_caixa_lib_w
from	usuario_caixa a
where	coalesce(a.nm_usuario_param::text, '') = ''
and	coalesce(a.nr_seq_caixa,nr_seq_caixa_p)	= nr_seq_caixa_p;

qt_caixa_reg_w := 1;

if (ie_usuario_caixa_w = 'N') then
	begin
	select	count(*)
	into STRICT	qt_caixa_reg_w
	from	usuario_caixa a
	where	a.cd_estabelecimento				= cd_estab_logado_w
	and	coalesce(a.nm_usuario_param, nm_usuario_param_p)	= nm_usuario_param_p
	and	coalesce(cd_perfil, cd_perfil_w)			= cd_perfil_w
	and	coalesce(a.nr_seq_caixa, nr_seq_caixa_p)		= nr_seq_caixa_p;
	end;
end if;

if (qt_caixa_reg_w = 0) then
	ie_liberado_w		:= 'N';
	ie_atualizar_retorno_w	:= 'N';
elsif (cont_w = 0) and
	((ie_usuario_caixa_w = 'L') or (qt_caixa_lib_w > 0 AND ie_usuario_caixa_w = 'N')) then
	ie_liberado_w		:= 'S';
	ie_atualizar_retorno_w	:= 'S';
else

	ie_liberado_w		:= 'N';
	ie_atualizar_retorno_w	:= 'N';

	open c01;
	loop
	fetch c01 into
		ie_atualizar_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ie_liberado_w		:= 'S';
		ie_atualizar_retorno_w	:= ie_atualizar_w;
	end loop;
	close c01;

end if;

if (ie_opcao_p = 0) then
	ds_retorno_w	:= ie_liberado_w;
elsif (ie_opcao_p = 1) then
	ds_retorno_w	:= ie_atualizar_retorno_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_usuario_caixa (nm_usuario_param_p text, nr_seq_caixa_p bigint, ie_opcao_p bigint) FROM PUBLIC;

