CREATE OR REPLACE FUNCTION obter_se_usuario_categoria ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_usuario_convenio_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(2)	:= 'S';
qt_registro_w		bigint;
nr_pos_inicial_w	smallint;
nr_pos_final_w		smallint;
cd_usuario_padrao_w	varchar(30);

C01 CURSOR FOR
	SELECT	nr_pos_inicial,
		nr_pos_final,
		cd_usuario_padrao
	from	conv_regra_usuario_categ
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
	and	cd_categoria		= cd_categoria_p;

BEGIN


select	count(*)
into STRICT	qt_registro_w
from	conv_regra_usuario_categ
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p
and	cd_categoria		= cd_categoria_p;

if (qt_registro_w	> 0) then

	open C01;
	loop
	fetch C01 into
		nr_pos_inicial_w,
		nr_pos_final_w,
		cd_usuario_padrao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (substr(cd_usuario_convenio_p,nr_pos_inicial_w,nr_pos_final_w)	<>
		cd_usuario_padrao_w) then
		ds_retorno_w	:= 'N';
	end if;

end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
