CREATE OR REPLACE FUNCTION obter_se_usuario_cnpj_fat ( cd_cnpj_exec_p text, cd_medico_p text default null) RETURNS varchar AS $body$
DECLARE

 
nr_seq_usuario_regra_w	bigint;	
nr_seq_regra_w		bigint;
retorno_w			varchar(1) := 'S';
nm_usuario_w		varchar(15);
cd_pessoa_fisica_w	varchar(15);		
 

BEGIN 
 
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
 
select 	Obter_Dados_Usuario_Opcao(nm_usuario_w,'C') 
into STRICT	cd_pessoa_fisica_w
;
 
Select 	max(nr_sequencia) 
into STRICT	nr_seq_regra_w 
from	med_fatur_regra 
where	cd_cnpj_exec = cd_cnpj_exec_p;
 
if (coalesce(nr_seq_regra_w::text, '') = '') then 
	Select 	max(nr_sequencia) 
	into STRICT	nr_seq_regra_w 
	from	med_fatur_regra 
	where	cd_medico = cd_medico_p;
end if;
 
 
if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then 
 
	retorno_w := 'N';
	 
	Select 	max(a.nr_sequencia) 
	into STRICT	nr_seq_usuario_regra_w 
	from	med_fatur_regra_item a, 
		med_fatur_regra b 
	where	b.nr_sequencia = nr_seq_regra_w	 
	and	b.nr_sequencia = a.nr_seq_regra 
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_w;
	 
	if (nr_seq_usuario_regra_w IS NOT NULL AND nr_seq_usuario_regra_w::text <> '') then	 
		retorno_w := 'S';	
	end if;
 
end if;
 
return retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
