SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_usuario_conectado ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_usr_conectado_w	varchar(1) := 'N';


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_usr_conectado_w
	from	gv$session
	where	program	= 'Tasy.exe'
	and	upper(substr(client_info,6,position(';' in client_info)-6)) = upper(nm_usuario_p);

end if;

return	ie_usr_conectado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
