CREATE OR REPLACE FUNCTION obter_se_usuario_dev_doc (nr_seq_motivo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_usuarios_motivo_w	bigint;
qt_reg_usuario_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_usuarios_motivo_w
from	motivo_dev_doc_usuario
where	nr_seq_motivo = nr_seq_motivo_p;

if (qt_usuarios_motivo_w = 0) then
	ie_retorno_w := 'S';
end if;

if (ie_retorno_w = 'N') then
	select	count(*)
	into STRICT	qt_reg_usuario_w
	from	motivo_dev_doc_usuario
	where	nr_seq_motivo = nr_seq_motivo_p
	and	nm_usuario_lib = nm_usuario_p;

	if (qt_reg_usuario_w <> 0) then
		ie_retorno_w := 'S';
	else
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
