CREATE OR REPLACE FUNCTION obter_se_usuario_evolucao ( nr_sequencia_p bigint, ie_evolucao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w 	varchar(1) := 'S';
ie_tipo_w		varchar(3);

BEGIN

select  coalesce(MAX(d.IE_PROFISSIONAL),'N')
into STRICT    ie_tipo_w
FROM 	PE_PRESCR_PROC d,
		PE_PRESCR_PROC_HOR e
WHERE  	e.nr_seq_pe_proc = d.nr_sequencia
and		e.nr_sequencia = nr_sequencia_p;

if ( ie_tipo_w <> 'N' and
	ie_tipo_w <> ie_evolucao_p) then
		ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
