CREATE OR REPLACE FUNCTION obter_se_usuario_exec_acao_os ( nr_seq_os_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_exec_acao_w	varchar(1) := 'N';
ie_tipo_exec_w	varchar(1);


BEGIN
if (nr_seq_os_p IS NOT NULL AND nr_seq_os_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_tipo_exec_w
	from	man_ordem_servico_exec
	where	nr_seq_ordem = nr_seq_os_p
	and	nm_usuario_exec = nm_usuario_p
	and	(nr_seq_tipo_exec IS NOT NULL AND nr_seq_tipo_exec::text <> '');

	if (ie_tipo_exec_w = 'N') then
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_exec_acao_w
		from	man_ordem_servico_exec
		where	nr_seq_ordem = nr_seq_os_p
		and	nm_usuario_exec = nm_usuario_p;
		end;
	else
		begin
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_exec_acao_w
		from	man_tipo_executor t,
			man_ordem_servico_exec e
		where	t.nr_sequencia = e.nr_seq_tipo_exec
		and	t.ie_tipo_executor in ('1','3')
		and	e.nr_seq_ordem = nr_seq_os_p
		and	e.nm_usuario_exec = nm_usuario_p;
		end;
	end if;
	end;
end if;
return ie_exec_acao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
