CREATE OR REPLACE FUNCTION obter_se_usuario_fechou_conta (nr_interno_conta_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15);
ds_retorno_w		varchar(15):= Null;

C01 CURSOR FOR
	SELECT	nm_usuario
	from	conta_paciente_hist
	where	nr_interno_conta = nr_interno_conta_p
	and	((nr_nivel_anterior = 2) or (nr_nivel_anterior = 4))
	and 	nr_nivel_atual = 6
	and	dt_atualizacao = (SELECT	max(dt_atualizacao)
					from	conta_paciente_hist
					where	nr_interno_conta = nr_interno_conta_p
					and	((nr_nivel_anterior = 2) or (nr_nivel_anterior = 4))
					and 	nr_nivel_atual = 6)
	order by nr_sequencia;


BEGIN

ds_retorno_w:= 'N';

open C01;
loop
fetch C01 into
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if nm_usuario_w = nm_usuario_p then
	ds_retorno_w:= 'S';
	end if;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
