CREATE OR REPLACE FUNCTION obter_se_usuario_item ( nr_seq_perfil_item_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_utiliza_w	varchar(1) := 'N';
ie_atualizar_w	varchar(1) := 'N';


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_utiliza_w
from	perfil_item_pepo_usuario
where	nr_seq_perfil_item = nr_seq_perfil_item_p
and	nm_usuario_liberacao = nm_usuario_p;

if (ie_utiliza_w = 'S') then
	select	coalesce(max(ie_atualizar),'N')
	into STRICT	ie_atualizar_w
	from	perfil_item_pepo_usuario
	where	nr_seq_perfil_item = nr_seq_perfil_item_p
	and	nm_usuario_liberacao = nm_usuario_p;
else
	select	coalesce(max(ie_atualizar),'S')
	into STRICT	ie_atualizar_w
	from	perfil_item_pepo
	where	nr_sequencia = nr_seq_perfil_item_p;
end if;

return ie_atualizar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
