CREATE OR REPLACE FUNCTION obter_se_usuario_lib_classif (nr_seq_classif_unidade_p bigint) RETURNS varchar AS $body$
DECLARE

   ds_retorno_w 		varchar(1) := 'S';
   cd_perfil_w		integer;
   nm_usuario_lib_w		varchar(15);
   nr_seq_regra_w		bigint;
   nr_seq_classif_unid_w	bigint;

c01 CURSOR FOR
SELECT       cd_perfil,
   	nm_usuario_lib,
	nr_sequencia
from 	reg_visual_classif_unid
where  	ie_situacao = 'A';

c02 CURSOR FOR
SELECT	nr_seq_classif_setor
from	reg_lib_classif_unid
where	ie_situacao = 'A'
and	nr_seq_regra = nr_seq_regra_w;


BEGIN

open c01;
   loop
   fetch c01 into
      cd_perfil_w,
      nm_usuario_lib_w,
      nr_seq_regra_w;
   EXIT WHEN NOT FOUND; /* apply on c01 */
   begin
      if((cd_perfil_w = obter_perfil_ativo and coalesce(nm_usuario_lib_w::text, '') = '')
      or (nm_usuario_lib_w = obter_usuario_ativo and coalesce(cd_perfil_w::text, '') = '')
      or (cd_perfil_w = obter_perfil_ativo and nm_usuario_lib_w = obter_usuario_ativo)) then
         open c02;
            loop
            fetch c02 into
               nr_seq_classif_unid_w;
            EXIT WHEN NOT FOUND; /* apply on c02 */
            begin
               if (coalesce(nr_seq_classif_unidade_p::text, '') = '') then
                  return 'N';
               end if;
               if (nr_seq_classif_unidade_p = nr_seq_classif_unid_w) then
                  return 'S';
               else
                  if (nr_seq_classif_unidade_p <> nr_seq_classif_unid_w) then
                     ds_retorno_w := 'N';
                  end if;
               end if;
            end;
            end loop;
         close c02;
      end if;
   end;
   end loop;
close c01;

return	ds_retorno_w;

end;
$body$
