CREATE OR REPLACE FUNCTION obter_se_usuario_lib_escala ( nr_sequencia_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	escala_usuario
where	nr_seq_escala = nr_sequencia_p;

if (qt_existe_w > 0) then
	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	escala_usuario
	where	nr_seq_escala	= nr_sequencia_p
	and	nm_usuario_lib	= nm_usuario_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
