CREATE OR REPLACE FUNCTION obter_se_usuario_medico ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
ie_medico_w		varchar(1);


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	obter_pessoa_fisica_usuario(nm_usuario_p, 'C')
	into STRICT	cd_pessoa_fisica_w
	;

	select	obter_se_medico(cd_pessoa_fisica_w, 'M')
