CREATE OR REPLACE FUNCTION obter_se_usuario_motivo_desc (nr_seq_motivo_desc_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


qt_usuario_lib_w	bigint;
ie_usuario_lib_w	varchar(1)	:= 'S';


BEGIN

select	count(*)
into STRICT	qt_usuario_lib_w
from	motivo_desc_usuario a
where	a.nr_seq_motivo	= nr_seq_motivo_desc_p;

if (qt_usuario_lib_w > 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_usuario_lib_w
	from	motivo_desc_usuario a
	where	a.nr_seq_motivo		= nr_seq_motivo_desc_p
	and	a.nm_usuario_lib	= nm_usuario_p;

end if;

RETURN ie_usuario_lib_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
