CREATE OR REPLACE FUNCTION obter_se_usuario_vis_item_pep (nr_seq_item_p bigint, cd_perfil_p bigint, ie_tipo_restricao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

if (ie_tipo_restricao_p = 'M') then

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	perfil_item_pront a,
	perfil_item_pront_lib b
where	a.nr_sequencia = b.nr_seq_item_perfil
and	a.nr_seq_item_pront = nr_seq_item_p
and	a.cd_perfil	= cd_perfil_p
and	upper(b.nm_usuario_param) = upper(nm_usuario_p);

elsif (ie_tipo_restricao_p = 'R') then

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	perfil_item_pront a,
	perfil_item_pront_lib b
where	a.nr_sequencia = b.nr_seq_item_perfil
and	a.nr_seq_item_pront = nr_seq_item_p
and	a.cd_perfil	= cd_perfil_p
and	upper(b.nm_usuario_param) = upper(nm_usuario_p);

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
