CREATE OR REPLACE FUNCTION obter_se_usu_resp_atendimento ( cd_pessoa_fisica_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(5);
ie_responsavel_w			varchar(5);
cd_pessoa_fisica_usuario_w	varchar(10);
nr_atendimento_w			varchar(10);

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	--- Verifica se ele é o médico responsável
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  ie_responsavel
	into STRICT 	ie_responsavel_w
	from	atendimento_paciente
	where	obter_dados_atendimento(nr_atendimento,'CP') = cd_pessoa_fisica_p
	and 	obter_usuario_pf(cd_medico_resp) = nm_usuario_p;
	--- Verifica se ele é o Auxiliar/Técnico responsável
	if (ie_responsavel_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  ie_responsavel
		into STRICT	ie_responsavel_w
		from	atend_enfermagem_resp a
		where	obter_dados_atendimento(a.nr_atendimento,'CP') = cd_pessoa_fisica_p
		and 	obter_usuario_pf(a.cd_pessoa_fisica) = nm_usuario_p;
	end if;
	ds_retorno_w := ie_responsavel_w;
end if;
 return coalesce(ds_retorno_w,'N');
 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
