CREATE OR REPLACE FUNCTION obter_se_utiliza_periodo (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_utiliza_periodo_w	parametro_agenda.IE_PERIODO_LISTA_ESPERA%type;


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	SELECT	coalesce(IE_PERIODO_LISTA_ESPERA,'N')
	into STRICT	ie_utiliza_periodo_w
	FROM	parametro_agenda
	WHERE	cd_estabelecimento = cd_estabelecimento_p;
end if;

return	ie_utiliza_periodo_w;

end;
$body$
LANGUAGE PLPGSQL
