CREATE OR REPLACE FUNCTION obter_se_utiliza_wint ( cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w 	varchar(1) := 'N';

BEGIN

if ((coalesce(cd_convenio_p,0) > 0) and (coalesce(cd_estabelecimento_p,0) > 0)) then
	
	begin
		select 	'S'
		into STRICT	ds_retorno_w
		from 	convenio_estabelecimento b
		where 	b.cd_convenio = cd_convenio_p
		and	b.cd_estabelecimento = cd_estabelecimento_p
		and	coalesce(b.ie_utiliza_wint,'N') = 'S'  LIMIT 1;
		
	exception
	when others then
		ds_retorno_w := 'N';
	end;
	
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
