SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_util_integra ( cd_estab_p bigint, ie_integracao_p text) RETURNS varchar AS $body$
DECLARE


cd_cgc_cliente_w	varchar(14);
ie_retorno_w		varchar(1);




BEGIN
select	max(obter_cgc_estabelecimento(cd_estab_p))
into STRICT	cd_cgc_cliente_w
;
ie_retorno_w := 'N';

if (cd_cgc_cliente_w = '01950338000177') then
	ie_retorno_w := 'S';
else
	SELECT	coalesce(MAX('S'),'N')
	into STRICT    ie_retorno_w
	FROM	LIBERACAO_ACESSO_EXT_PEP
	WHERE   CD_CGC_CLIENTE = cd_cgc_cliente_w
	and 	coalesce(ie_sistema_externo,'XPTO')	= ie_integracao_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
