CREATE OR REPLACE FUNCTION obter_se_util_status ( ie_status_p text) RETURNS varchar AS $body$
DECLARE


ie_status_w	varchar(10);


BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_status_w
from	gqa_pendencia_regra
where	ie_regra_sepse = ie_status_p
and	nr_seq_escala = 124;

return	ie_status_w;

end;
