CREATE OR REPLACE FUNCTION obter_se_validade_anvisa ( cd_material_p bigint, dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


dt_validade_reg_anvisa_w	timestamp;
dt_validade_prot_anvisa_w	timestamp;
ie_valido_w 			varchar(1):= 'S';
dt_referencia_w			timestamp;
ie_vigente_anvisa_w		material_estab.ie_vigente_anvisa%type;


BEGIN

dt_referencia_w := coalesce(dt_referencia_p,clock_timestamp());

dt_validade_reg_anvisa_w 	:= obter_dados_material_estab(cd_material_p,cd_estabelecimento_p,'VA');
dt_validade_prot_anvisa_w	:= obter_dados_material_estab(cd_material_p,cd_estabelecimento_p,'VPA');
ie_vigente_anvisa_w		:= obter_dados_material_estab(cd_material_p,cd_estabelecimento_p,'VI');



if (ie_vigente_anvisa_w = 'N') and (dt_validade_reg_anvisa_w IS NOT NULL AND dt_validade_reg_anvisa_w::text <> '') and (dt_validade_reg_anvisa_w < trunc(dt_referencia_w)) then

	if (coalesce(dt_validade_prot_anvisa_w::text, '') = '') or (dt_validade_prot_anvisa_w < trunc(dt_referencia_w)) then
		ie_valido_w := 'N';
	end if;
end if;

return	ie_valido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
