CREATE OR REPLACE FUNCTION obter_se_valor_coluna (nm_tabela_p text, nm_coluna_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_comando_w	varchar(240);
qt_valor_w	double precision;
ie_valor_w	varchar(1) := 'N';


BEGIN
if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nm_coluna_p IS NOT NULL AND nm_coluna_p::text <> '') then
	ds_comando_w := 'select count(*) from '||nm_tabela_p ||' where ' || nm_coluna_p || ' is not null and nm_usuario = ' || chr(39) || nm_usuario_p || chr(39);

