CREATE OR REPLACE FUNCTION obter_se_valor_contido ( ds_valor_p text, ds_valores_p text) RETURNS char AS $body$
DECLARE


-- Para esta function
-- DS_VALOR_P	 := valor definitivo (sem vírgula)
-- DS_VALORES_P := valores definitivos possíveis separados por vírgula
BEGIN

if (position(',' || ds_valor_p || ',' in ',' || ds_valores_p || ',') > 0) then
	return 'S';
end if;

return	'N';

end;
