CREATE OR REPLACE FUNCTION obter_se_valor_dominio_ativo (cd_dominio_p bigint, vl_dominio_p text) RETURNS varchar AS $body$
DECLARE


	ie_ativo_w varchar(1) := 'S';


BEGIN
  if (cd_dominio_p IS NOT NULL AND cd_dominio_p::text <> '') and (vl_dominio_p IS NOT NULL AND vl_dominio_p::text <> '') then

	select  coalesce(max('S'),'N')
	into STRICT	ie_ativo_w
	from	valor_dominio
	where	cd_dominio = cd_dominio_p
	and		vl_dominio = vl_dominio_p
	and		coalesce(ie_situacao,'A') = 'A';


  end	if;

  return ie_ativo_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
