SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_valor_periodo ( nr_titulo_p bigint, ie_opcao_p text, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p

CP	Contas a Pagar
CR	Contas a Receber

*/
dt_vencimento_w		timestamp;
ds_retorno_w		varchar(1);


BEGIN

ds_retorno_w	:= 'N';

if (ie_opcao_p	= 'CP') then

	select	max(a.dt_vencimento_atual)
	into STRICT	dt_vencimento_w
	from	titulo_pagar a
	where	a.nr_titulo	= nr_titulo_p;

elsif (ie_opcao_p	= 'CR') then

	select	max(a.dt_pagamento_previsto)
	into STRICT	dt_vencimento_w
	from	titulo_receber a
	where	a.nr_titulo	= nr_titulo_p;

end if;

if (dt_vencimento_w >= dt_inicial_p) and (dt_vencimento_w <= dt_final_p) then
	ds_retorno_w	:= 'S';
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
