CREATE OR REPLACE FUNCTION obter_se_valor_rend_superior (cd_pessoa_fisica_p text, cd_cgc_p text, dt_lote_p timestamp, vl_min_ano_pf_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_superior_w		varchar(2);
vl_rendimentos_w	double precision;


BEGIN


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	if (coalesce(vl_min_ano_pf_p::text, '') = '') then
		ie_superior_w := 'S';
	else
		vl_rendimentos_w := obter_total_tit_pag(cd_pessoa_fisica_p,cd_cgc_p,dt_lote_p);
		if (vl_rendimentos_w > vl_min_ano_pf_p) then
			ie_superior_w := 'S';
		else
			ie_superior_w := 'N';
		end if;
	end if;
else
	ie_superior_w := 'N';
end if;

return ie_superior_w;

end;
