CREATE OR REPLACE FUNCTION obter_se_versao_cid_vigente (cd_doenca_cid_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_retorno_w  varchar(1);


BEGIN

  if coalesce(dt_referencia_p::text, '') = '' then
    ds_retorno_w := 'S';
  else
   begin
    select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
    into STRICT ds_retorno_w
    from cid_doenca cd,
         cid_doenca_versao cdv
    where cd.cd_doenca_cid = cdv.cd_doenca_cid
    and cd.cd_doenca_cid =  cd_doenca_cid_p
    and dt_referencia_p between cdv.dt_vigencia_inicial and coalesce(cdv.dt_vigencia_final,clock_timestamp());
   end;
  end if;

  return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
