CREATE OR REPLACE FUNCTION obter_se_versao_vigente (cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE

retorno_w	varchar(1):= 'S';


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then

select CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
into STRICT retorno_w
from procedimento_versao proc
where proc.cd_procedimento = cd_procedimento_p
and proc.ie_origem_proced = ie_origem_proced_p
and dt_referencia_p between proc.dt_vigencia_inicial and coalesce(proc.dt_vigencia_final,clock_timestamp());

end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
