CREATE OR REPLACE FUNCTION obter_se_viagem_consult (cd_consultor_p text, dt_parametro_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

select	CASE WHEN coalesce(b.nm_curto_agenda::text, '') = '' THEN '      '  ELSE ' A ' END   || coalesce(( select 	CASE WHEN count(*)=0 THEN  ' '  ELSE 'V' END  ie_agen_viag
								from	via_viagem x, VIA_RESERVA  y
								where	x.nr_sequencia	= y.nr_seq_viagem
								and	((trunc(x.DT_SAIDA_PREV)		= trunc(a.dt_agenda)) or (trunc(x.DT_RETORNO_PREV)		= trunc(a.dt_agenda)))
								and	x.cd_pessoa_fisica	= a.cd_consultor
								),'  ')  nm_curto_agenda
into STRICT	ds_retorno_w
FROM proj_agenda a
LEFT OUTER JOIN proj_projeto b ON (a.nr_seq_proj = b.nr_sequencia)
WHERE a.cd_consultor 	= cd_consultor_p and a.ie_status not in ('Suspensa','Bloqueada','Cancelada') and trunc(dt_agenda,'dd') = dt_parametro_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
