CREATE OR REPLACE FUNCTION obter_se_via_adm_medic ( cd_material_p bigint, ie_via_adm_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
qt_via_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_via_w
from	mat_via_aplic
where	cd_material		= cd_material_p
and	ie_via_aplicacao	= ie_via_adm_p  LIMIT 1;

if (qt_via_w > 0) then
	ie_retorno_w	:= 'S';
else
	begin
	select	count(1)
	into STRICT	qt_via_w
	from	material
	where	cd_material		= cd_material_p
	and	ie_via_aplicacao	= ie_via_adm_p;

	if (qt_via_w > 0) then
		ie_retorno_w	:= 'S';
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
