CREATE OR REPLACE FUNCTION obter_se_via_adm ( cd_material_p bigint, ie_via_adm_p text) RETURNS char AS $body$
DECLARE


ie_retorno_w	char(1) := 'S';

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select	coalesce(max('N'), 'S')
	into STRICT	ie_retorno_w
	from	mat_via_aplic where		cd_material	= cd_material_p
	and		coalesce(cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento LIMIT 1;

	if (ie_retorno_w = 'N') then
		begin
		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	material
		where	cd_material	= cd_material_p
		and		ie_via_aplicacao = ie_via_adm_p;

		if (ie_retorno_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_retorno_w
			from	mat_via_aplic where		cd_material	= cd_material_p
			and		ie_via_aplicacao = ie_via_adm_p LIMIT 1;
		end if;
		end;
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
