CREATE OR REPLACE FUNCTION obter_se_vigente_tuss ( dt_item_p timestamp, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


dt_inicio_vig_tuss_w	timestamp;


BEGIN

select	max(coalesce(dt_inicio_vig_tuss, clock_timestamp()))
into STRICT	dt_inicio_vig_tuss_w
from	convenio_estabelecimento
where	cd_convenio 		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p;

if (dt_item_p < dt_inicio_vig_tuss_w) then
	return 1;
else
	return 2;
end if;

end;
$body$
