CREATE OR REPLACE FUNCTION obter_se_vinculo_cadastro (cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_cadastro_w		varchar(2);
qt_reg_w					bigint;
ie_vinculo_medico_w			bigint;


BEGIN

ie_permite_cadastro_w		:= 'S';

select 	count(*)
into STRICT	qt_reg_w
from	REGRA_VINCULO_CADASTRO_PF;

if (qt_reg_w > 0) then
	begin

	select	max(ie_vinculo_medico)
	into STRICT	ie_vinculo_medico_w
	from	medico
	where	cd_pessoa_fisica	= cd_medico_p;

	select 	coalesce(max(IE_PERMITE_MANUTENCAO), 'S')
	into STRICT	ie_permite_cadastro_w
	from	REGRA_VINCULO_CADASTRO_PF
	where	ie_vinculo_medico	= ie_vinculo_medico_w;

	end;
end if;

return	ie_permite_cadastro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
