SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_vinc_medico_lib ( ie_vinculo_medico_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1)	:= 'S';
cd_setor_w			bigint;
cd_perfil_w			bigint;
cd_perfil_ativo_w			bigint;
nm_usuario_lib_w			varchar(15);
qt_registro_w			bigint;

c01 CURSOR FOR
SELECT	coalesce(a.ie_permite,'N') ie_permite,
	a.cd_perfil,
	a.cd_setor,
	a.nm_usuario_lib
from	regra_vinc_medico_lib a
where	ie_vinculo_medico			= ie_vinculo_medico_p
and	coalesce(cd_perfil, cd_perfil_ativo_w)	= cd_perfil_ativo_w
and	coalesce(nm_usuario_lib, nm_usuario_p)	= nm_usuario_p
order by	coalesce(nm_usuario_lib,'A'),
		coalesce(cd_perfil,0),
		coalesce(cd_setor,0);



BEGIN

select	count(*)
into STRICT	qt_registro_w
from	regra_vinc_medico_lib;

begin
cd_perfil_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
exception when others then
	cd_perfil_ativo_w	:= 0;
end;

if (qt_registro_w > 0) then

	ie_permite_w	:= 'N';

	open C01;
	loop
	fetch C01 into
		ie_permite_w,
		cd_perfil_w,
		cd_setor_w,
		nm_usuario_lib_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	usuario_perfil b
			where	b.cd_perfil	= cd_perfil_w
			and	b.nm_usuario	= nm_usuario_p;

		end if;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_setor_w IS NOT NULL AND cd_setor_w::text <> '') then

			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	usuario_setor_v b
			where	b.cd_setor_atendimento	= cd_setor_w
			and	b.nm_usuario		= nm_usuario_p;

		end if;

		ie_permite_w	:= ie_permite_w;
		end;
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
