CREATE OR REPLACE FUNCTION obter_se_visitante_lib_perfil (nr_seq_visitante_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(5);
qt_visitante_w           integer;
cd_perfil_w	  bigint;


BEGIN

cd_perfil_w := 	obter_perfil_ativo;

select  count(*)
into STRICT      qt_visitante_w
from     regra_visitante_perfil
where  nr_seq_visitante = nr_seq_visitante_p;

ds_retorno_w := 'S';

if (qt_visitante_w > 0) then

        select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
        into STRICT     ds_retorno_w
        from    regra_visitante_perfil
        where nr_seq_visitante = nr_seq_visitante_p
       and      coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;

end if;
return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
