SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_visita_hospitalidade ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint;
qt_tipo_visita_w		bigint;


BEGIN

select 	count(1)
into STRICT	qt_tipo_visita_w
from	tipo_evolucao
where	ie_evolucao_visita = 'S'
and		ie_situacao = 'A';

if (qt_tipo_visita_w > 0) then
	select	count(*)
	into STRICT	qt_registro_w
	from	evolucao_paciente a,
			tipo_evolucao b
	where	a.ie_evolucao_clinica = b.cd_tipo_evolucao
	and 	a.nr_atendimento		= nr_atendimento_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_inativacao::text, '') = ''
	and 	b.ie_evolucao_visita = 'S'
	and		a.cd_medico	in	(
				SELECT		cd_pessoa_fisica
				from		atend_profissional
				where	nr_atendimento		= nr_atendimento_p
				and(( ie_profissional		= 'H') or (IS_COPY_BASE_LOCALE('es_MX') = 'S' and ie_profissional = '12'))
			);
else
	select	count(*)
	into STRICT	qt_registro_w
	from	evolucao_paciente
	where	nr_atendimento		= nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_inativacao::text, '') = ''
	and	cd_medico	in	(
		SELECT		cd_pessoa_fisica
		from		atend_profissional
		where	nr_atendimento		= nr_atendimento_p
		and(( ie_profissional		= 'H') or (IS_COPY_BASE_LOCALE('es_MX') = 'S' and ie_profissional = '12'))
	);
end if;

if (qt_registro_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_visita_hospitalidade ( nr_atendimento_p bigint) FROM PUBLIC;

