CREATE OR REPLACE FUNCTION obter_se_visualiza_cancel ( qt_horas_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ie_visualiza_w		varchar(1) := 'S';
dt_cancelamento_w 	timestamp;


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	select max(dt_cancelamento)
	into STRICT   dt_cancelamento_w
	from   agenda_paciente
	where  nr_sequencia = nr_seq_agenda_p;

	if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
		if (dt_cancelamento_w > clock_timestamp() - (qt_horas_p/24)) then
			ie_visualiza_w	:= 'S';
		else
		       	 ie_visualiza_w	:= 'N';
		end if;
	end if;
end if;

return	ie_visualiza_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
