CREATE OR REPLACE FUNCTION obter_se_visualiza_item_oft (nr_seq_consulta_p bigint, nr_seq_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_icone_w		varchar(1)	:= 'N';
nm_usuario_w		varchar(15) := Wheb_usuario_pck.get_nm_usuario;
cd_perfil_w		bigint   := wheb_usuario_pck.get_cd_perfil;
qt_regra_w		integer;
nr_seq_tipo_consulta_w	bigint;


BEGIN

select max(nr_seq_tipo_consulta)
into STRICT   nr_seq_tipo_consulta_w
from   oft_consulta
where  nr_sequencia =  nr_seq_consulta_p;

select count(*)
into STRICT   qt_regra_w
from   oft_tipo_consulta_item
where  nr_seq_tipo_consulta = nr_seq_tipo_consulta_w;

if (qt_regra_w > 0) then
	SELECT coalesce(MAX('S'),'N')
	into STRICT   ie_icone_w
	from   oft_tipo_consulta a
	where  nr_sequencia = nr_seq_tipo_consulta_w
	and    nr_seq_item_p in ( SELECT x.nr_seq_item
				  from oft_tipo_consulta_item x
				  where x.nr_seq_tipo_consulta = a.nr_sequencia);

end if;

return	ie_icone_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
