SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_visualiza_proj_rec ( nr_seq_proj_rec_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1) := 'S';
ie_proj_rec_liberado_w		varchar(1);
ie_proj_rec_vigente_w		varchar(1);
qt_existe_w			bigint;
cd_estabelecimento_w		integer := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

select	coalesce(max(ie_proj_rec_liberado),'N'),
	coalesce(max(ie_proj_rec_vigente),'N')
into STRICT	ie_proj_rec_liberado_w,
	ie_proj_rec_vigente_w
from	parametro_compras
where	cd_estabelecimento = cd_estabelecimento_w;

if (ie_permite_w = 'S') and (ie_proj_rec_vigente_w = 'S') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	projeto_recurso_vigencia
	where	nr_seq_projeto = nr_seq_proj_rec_p;

	if (qt_existe_w > 0) then
		begin

		select	count(*)
		into STRICT	qt_existe_w
		from	projeto_recurso_vigencia
		where	nr_seq_projeto = nr_seq_proj_rec_p
		and	trunc(clock_timestamp()) between coalesce(dt_inicio_exec,clock_timestamp() - interval '1 days') and coalesce(dt_fim_exec,clock_timestamp() + interval '1 days');

		if (qt_existe_w = 0) then
			ie_permite_w	:= 'N';
		end if;

		end;
	end if;
	end;
end if;

if (ie_permite_w = 'S') and (ie_proj_rec_liberado_w = 'S') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	projeto_recurso
	where	nr_sequencia = nr_seq_proj_rec_p
	and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');

	if (qt_existe_w = 0) then
		ie_permite_w	:= 'N';
	end if;

	end;
end if;

return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
