CREATE OR REPLACE FUNCTION obter_se_vis_evol_nao_lib (cd_perfil_p bigint, cd_tipo_evolucao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_Registros_w	smallint;


BEGIN

select  count(1)
into STRICT	qt_registros_w
from	TIPO_EVOLUCAO_REGRA_LIB
where	cd_perfil 	 = cd_perfil_p
and	cd_tipo_evolucao = cd_tipo_evolucao_p;

If (qt_registros_w > 0) Then
	 ds_retorno_w := 'S';
Else	 ds_retorno_w := 'N';
End If;

return	ds_retorno_w;

end;
$body$
